def add(num1, num2):
    return num1 + num2
def sub(num1, num2):
    return num1 - num2
def mul(num1, num2):
    return num1 * num2
def div(num1, num2):
    return num1 / num2
def exp(num1, num2):
    return num1 ** num2
def sqt(num1):
    sqt = num1 ** (1/2)
    return sqt

print(add(7,8))
print(div(4,5))
print(exp(4,3))
print(sqt(25))

