#!/usr/bin/env python
"""
This is the unittest of SRF class.
"""

import unittest

import numpy as np

import gstools_cython as gs_cy


class TestField(unittest.TestCase):
    def test_summate(self):
        # x = np.linspace(0,1,5)
        # mod = gs.Gaussian(dim=1)
        # srf = gs.SRF(mod, mode_no=10, seed=1234)
        # srf(x)
        cov_samples = np.array(
            [
                [
                    -0.49995807783373075,
                    -0.7820163721559825,
                    1.690118803237597,
                    1.9756208177659687,
                    0.03945771863093044,
                    2.127277879098216,
                    -1.4576342168089562,
                    0.5947643837384975,
                    0.09811641644885175,
                    -0.003565139481429214,
                ]
            ],
            dtype=np.double,
        )
        z_1 = np.array(
            [
                0.9946988048718556,
                1.814210031079757,
                1.1686180785678166,
                -0.374250826058506,
                1.208282071166948,
                1.044190549877576,
                -1.7850832797400267,
                1.1341225325719555,
                0.005871108068883179,
                0.4918288313002647,
            ],
            dtype=np.double,
        )
        z_2 = np.array(
            [
                0.38320431788616655,
                -0.6158908046660203,
                -1.5221152986631148,
                0.4213284409858781,
                -1.2858750366939806,
                -0.1286138436420879,
                -0.031244435678407644,
                0.16914501403169677,
                -0.04578215996092473,
                -0.48668407941054204,
            ],
            dtype=np.double,
        )
        pos = np.array([[0.0, 0.25, 0.5, 0.75, 1.0]], dtype=np.double)
        summed_modes = np.array(
            [5.7024879, 5.16758284, 4.46056939, 3.73413573, 3.14911511], dtype=np.double
        )
        summed = gs_cy.field.summate(cov_samples, z_1, z_2, pos)
        np.testing.assert_allclose(summed_modes, summed)

